class Josephus {

	constructor() {
        //do stuff
    }

	static josephus(n, k) {
		if (k < 1) {
			return;
		}
		let circle = [];
		let effectiveCount = n;
		for(let i=0; i < n; ++i) {
			circle.push(i+1);
		}
		console.log(circle);
		let pos = 0;

		while(circle.length > 1) {
			pos = (pos + k -1)
			if (pos >= effectiveCount) {
				console.log(pos, effectiveCount);
				pos = (pos % effectiveCount);
				effectiveCount = circle.length;
				console.log(pos, effectiveCount);
			} 
			sleep(10);
			console.log('removing element ', circle.splice(pos , 1));
			sleep(10);
			
		}


	}
};

